import React, { useState, useEffect } from "react";
import "./prescriptionform.css";
import axios from "axios";

const PrescriptionForm = ({ doctorId, patientId }) => {
  const [prescriptionData, setPrescriptionData] = useState({
    medicines: [{ medicineName: "", dosage: "", frequency: "", quantity: "", instructions: "" }],
    dateIssued: new Date().toISOString().split("T")[0],
    validUntil: "",
    notes: "",
  });
  const [doctorName, setDoctorName] = useState("");
  const [patientName, setPatientName] = useState("");

  useEffect(() => {
    // Fetch doctor and patient details
    const fetchUserDetails = async () => {
      try {
        const doctorRes = await axios.get(`/api/users/${doctorId}`);
        setDoctorName(doctorRes.data.name);
        
        const patientRes = await axios.get(`/api/users/${patientId}`);
        setPatientName(patientRes.data.name);
      } catch (error) {
        console.error("Error fetching user details", error);
      }
    };
    fetchUserDetails();
  }, [doctorId, patientId]);

  const handleInputChange = (e, field, index) => {
    const { name, value } = e.target;
    if (name === "medicines") {
      const updatedMedicines = [...prescriptionData.medicines];
      updatedMedicines[index][field] = value;
      setPrescriptionData({ ...prescriptionData, medicines: updatedMedicines });
    } else {
      setPrescriptionData({ ...prescriptionData, [name]: value });
    }
  };

  const addMedicine = () => {
    setPrescriptionData({
      ...prescriptionData,
      medicines: [
        ...prescriptionData.medicines,
        { medicineName: "", dosage: "", frequency: "", quantity: "", instructions: "" },
      ],
    });
  };

  const removeMedicine = (index) => {
    const updatedMedicines = prescriptionData.medicines.filter((_, i) => i !== index);
    setPrescriptionData({ ...prescriptionData, medicines: updatedMedicines });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/prescriptions", {
        ...prescriptionData,
        doctorId,
        patientId,
      });
      alert("Prescription submitted successfully!");
    } catch (error) {
      console.error("Error submitting prescription", error);
    }
  };

  return (
    <div className="container">
      <div className="prescription-card">
        <h2>Create Prescription</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Doctor Name:</label>
            <input type="text" value={doctorName} disabled />
          </div>
          <div className="form-group">
            <label>Patient Name:</label>
            <input type="text" value={patientName} disabled />
          </div>
          <div className="form-group">
            <label>Date Issued:</label>
            <input type="date" name="dateIssued" value={prescriptionData.dateIssued} onChange={handleInputChange} />
          </div>
          <div className="form-group">
            <label>Valid Until:</label>
            <input type="date" name="validUntil" value={prescriptionData.validUntil} onChange={handleInputChange} required />
          </div>
          <div className="medicines-section">
            {prescriptionData.medicines.map((med, index) => (
              <div key={index} className="medicine">
                <input type="text" name="medicines" value={med.medicineName} onChange={(e) => handleInputChange(e, "medicineName", index)} required placeholder="Medicine Name" />
                <input type="text" name="medicines" value={med.dosage} onChange={(e) => handleInputChange(e, "dosage", index)} required placeholder="Dosage" />
                <input type="text" name="medicines" value={med.frequency} onChange={(e) => handleInputChange(e, "frequency", index)} placeholder="Frequency" />
                <input type="number" name="medicines" value={med.quantity} onChange={(e) => handleInputChange(e, "quantity", index)} required placeholder="Quantity" />
                <textarea name="medicines" value={med.instructions} onChange={(e) => handleInputChange(e, "instructions", index)} placeholder="Instructions" />
                {prescriptionData.medicines.length > 1 && <button type="button" onClick={() => removeMedicine(index)}>Remove</button>}
              </div>
            ))}
            <button type="button" onClick={addMedicine} className="add-medicine">Add Medicine</button>
          </div>
          <div className="form-group">
            <label>Notes:</label>
            <textarea name="notes" value={prescriptionData.notes} onChange={handleInputChange} placeholder="Enter additional notes" required />
          </div>
          <button type="submit" className="submit-button">Submit Prescription</button>
        </form>
      </div>
    </div>
  );
};

export default PrescriptionForm;
