import React, { useState } from 'react';
import './prescriptionform.css';

const PrescriptionForm = () => {
  const [prescription, setPrescription] = useState({
    medicines: [
      {
        name: '',
        dosage: '',
        quantity: '',
        frequency: '',
        instructions: ''
      }
    ],
    notes: ''
  });

  // Update specific field of a medicine in the list
  const updateMedicine = (index, field, value) => {
    const updatedMedicines = [...prescription.medicines];
    updatedMedicines[index][field] = value;
    setPrescription({ ...prescription, medicines: updatedMedicines });
  };

  // Add a new medicine to the list
  const addMedicine = () => {
    setPrescription({
      ...prescription,
      medicines: [
        ...prescription.medicines,
        {
          name: '',
          dosage: '',
          quantity: '',
          frequency: '',
          instructions: ''
        }
      ]
    });
  };

  // Handle the form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitted Prescription:', prescription);
    alert('Prescription has been submitted!');
  };

  return (
    <div className="prescription-form-container">
      <h2>Prescription Form</h2>
      <p>Enter the prescription details below:</p>

      <form onSubmit={handleSubmit}>
        {/* Medicines Section */}
        {prescription.medicines.map((medicine, index) => (
          <div key={index} className="medicine-entry">
            <h3>Medicine #{index + 1}</h3>

            <div className="input-group">
              <label>Medicine Name:</label>
              <input
                type="text"
                value={medicine.name}
                onChange={(e) => updateMedicine(index, 'name', e.target.value)}
                placeholder="Enter Medicine Name"
                required
              />
            </div>

            <div className="input-row">
              <div className="input-group">
                <label>Dosage:</label>
                <input
                  type="text"
                  value={medicine.dosage}
                  onChange={(e) => updateMedicine(index, 'dosage', e.target.value)}
                  placeholder="Enter Dosage"
                  required
                />
              </div>

              <div className="input-group">
                <label>Quantity:</label>
                <input
                  type="number"
                  value={medicine.quantity}
                  onChange={(e) => updateMedicine(index, 'quantity', e.target.value)}
                  placeholder="Enter Quantity"
                  required
                />
              </div>
            </div>

            <div className="input-row">
              <div className="input-group">
                <label>Frequency:</label>
                <input
                  type="text"
                  value={medicine.frequency}
                  onChange={(e) => updateMedicine(index, 'frequency', e.target.value)}
                  placeholder="Enter Frequency"
                  required
                />
              </div>

              <div className="input-group">
                <label>Instructions:</label>
                <input
                  type="text"
                  value={medicine.instructions}
                  onChange={(e) => updateMedicine(index, 'instructions', e.target.value)}
                  placeholder="Enter Instructions"
                  required
                />
              </div>
            </div>

            {/* Option to remove medicine entry */}
            {index > 0 && (
              <button
                type="button"
                className="remove-medicine-btn"
                onClick={() => {
                  const updatedMedicines = prescription.medicines.filter((_, i) => i !== index);
                  setPrescription({ ...prescription, medicines: updatedMedicines });
                }}
              >
                Remove Medicine
              </button>
            )}
          </div>
        ))}

        {/* Add new medicine button */}
        <button type="button" className="add-medicine-btn" onClick={addMedicine}>
          Add Another Medicine
        </button>

        {/* Notes Section */}
        <div className="input-group">
          <label>Notes:</label>
          <textarea
            value={prescription.notes}
            onChange={(e) => setPrescription({ ...prescription, notes: e.target.value })}
            placeholder="Enter any additional notes"
            required
          ></textarea>
        </div>

        {/* Submit button */}
        <button type="submit" className="submit-btn">
          Submit Prescription
        </button>
      </form>
    </div>
  );
};

export default PrescriptionForm;
