import React, { useState } from "react";
import './prescriptionform.css';

const PrescriptionForm = () => {
  const [medicines, setMedicines] = useState([
    { medicineName: "", dosage: "", quantity: "", frequency: "", instructions: "" },
  ]);
  const [patientId, setPatientId] = useState("");
  const [doctorId, setDoctorId] = useState("");
  const [validUntil, setValidUntil] = useState("");
  const [notes, setNotes] = useState("");

  // Handle medicine input changes
  const handleMedicineChange = (index, event) => {
    const updatedMedicines = [...medicines];
    updatedMedicines[index][event.target.name] = event.target.value;
    setMedicines(updatedMedicines);
  };

  // Add a new medicine entry
  const addMedicine = () => {
    setMedicines([
      ...medicines,
      { medicineName: "", dosage: "", quantity: "", frequency: "", instructions: "" },
    ]);
  };

  // Remove a medicine entry
  const removeMedicine = (index) => {
    const updatedMedicines = medicines.filter((_, i) => i !== index);
    setMedicines(updatedMedicines);
  };

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();

    const prescription = {
      medicines,
      patientId,
      doctorId,
      validUntil,
      notes,
    };

    try {
      const response = await fetch('http://localhost:5000/api/prescription', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(prescription),
      });

      if (response.ok) {
        const data = await response.json();
        alert('Prescription uploaded successfully!');
        console.log('Prescription Submitted:', data);
      } else {
        alert('Failed to upload prescription!');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while submitting the prescription.');
    }
  };

  return (
    <div className="form-container">
      <h2 className="form-title">Prescription Form</h2>
      <form onSubmit={handleSubmit} className="prescription-form">
        {/* Patient and Doctor ID Fields */}
        <div className="input-group">
          <label htmlFor="patientId">Patient ID:</label>
          <input
            type="text"
            id="patientId"
            name="patientId"
            value={patientId}
            onChange={(e) => setPatientId(e.target.value)}
            className="input-field"
            required
          />
        </div>

        <div className="input-group">
          <label htmlFor="doctorId">Doctor ID:</label>
          <input
            type="text"
            id="doctorId"
            name="doctorId"
            value={doctorId}
            onChange={(e) => setDoctorId(e.target.value)}
            className="input-field"
            required
          />
        </div>

        {/* Medicines Section */}
        <div className="medicines-section">
          {medicines.map((medicine, index) => (
            <div key={index} className="medicine-entry">
              <div className="input-group">
                <label htmlFor={`medicineName-${index}`}>Medicine Name:</label>
                <input
                  type="text"
                  id={`medicineName-${index}`}
                  name="medicineName"
                  value={medicine.medicineName}
                  onChange={(e) => handleMedicineChange(index, e)}
                  className="input-field"
                  required
                />
              </div>

              <div className="input-group">
                <label htmlFor={`dosage-${index}`}>Dosage:</label>
                <input
                  type="text"
                  id={`dosage-${index}`}
                  name="dosage"
                  value={medicine.dosage}
                  onChange={(e) => handleMedicineChange(index, e)}
                  className="input-field"
                  required
                />
              </div>

              <div className="input-group">
                <label htmlFor={`quantity-${index}`}>Quantity:</label>
                <input
                  type="number"
                  id={`quantity-${index}`}
                  name="quantity"
                  value={medicine.quantity}
                  onChange={(e) => handleMedicineChange(index, e)}
                  className="input-field"
                  required
                />
              </div>

              <div className="input-group">
                <label htmlFor={`frequency-${index}`}>Frequency:</label>
                <input
                  type="text"
                  id={`frequency-${index}`}
                  name="frequency"
                  value={medicine.frequency}
                  onChange={(e) => handleMedicineChange(index, e)}
                  className="input-field"
                  required
                />
              </div>

              <div className="input-group">
                <label htmlFor={`instructions-${index}`}>Instructions:</label>
                <input
                  type="text"
                  id={`instructions-${index}`}
                  name="instructions"
                  value={medicine.instructions}
                  onChange={(e) => handleMedicineChange(index, e)}
                  className="input-field"
                  required
                />
              </div>

              {/* Remove Medicine Button */}
              {medicines.length > 1 && (
                <button
                  type="button"
                  className="remove-btn"
                  onClick={() => removeMedicine(index)}
                >
                  Remove Medicine
                </button>
              )}
            </div>
          ))}

          {/* Add Medicine Button */}
          <button type="button" className="add-btn" onClick={addMedicine}>
            Add Medicine
          </button>
        </div>

        {/* Valid Until and Notes Fields */}
        <div className="input-group">
          <label htmlFor="validUntil">Valid Until:</label>
          <input
            type="date"
            id="validUntil"
            name="validUntil"
            value={validUntil}
            onChange={(e) => setValidUntil(e.target.value)}
            className="input-field"
            required
          />
        </div>

        <div className="input-group">
          <label htmlFor="notes">Notes:</label>
          <textarea
            id="notes"
            name="notes"
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
            className="input-field"
            required
          ></textarea>
        </div>

        {/* Submit Button */}
        <button type="submit" className="submit-btn">
          Submit Prescription
        </button>
      </form>
    </div>
  );
};

export default PrescriptionForm;
