import React, { useState, useEffect } from 'react';
import './prescriptionform.css';

const PrescriptionForm = ({ onSubmit, initialData = null }) => {
  const [formData, setFormData] = useState({
    medicines: [
      {
        medicineName: '',
        dosage: '',
        quantity: '',
        frequency: '',
        instructions: ''
      }
    ],
    patientId: '',
    doctorId: '',
    validUntil: '',
    notes: ''
  });

  useEffect(() => {
    if (initialData) {
      setFormData(initialData);
    }
  }, [initialData]);

  const handleMedicineChange = (index, field, value) => {
    const updatedMedicines = [...formData.medicines];
    updatedMedicines[index] = {
      ...updatedMedicines[index],
      [field]: value
    };
    setFormData({ ...formData, medicines: updatedMedicines });
  };

  const addMedicine = () => {
    setFormData({
      ...formData,
      medicines: [
        ...formData.medicines,
        {
          medicineName: '',
          dosage: '',
          quantity: '',
          frequency: '',
          instructions: ''
        }
      ]
    });
  };

  const removeMedicine = (index) => {
    const updatedMedicines = formData.medicines.filter((_, i) => i !== index);
    setFormData({ ...formData, medicines: updatedMedicines });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit} className="prescription-form">
      <h2>Create Prescription</h2>

      <div className="form-section">
        <h3>Medicines</h3>
        {formData.medicines.map((medicine, index) => (
          <div key={index} className="medicine-entry">
            <div className="medicine-header">
              <h4>Medicine {index + 1}</h4>
              {formData.medicines.length > 1 && (
                <button 
                  type="button" 
                  onClick={() => removeMedicine(index)}
                  className="remove-btn"
                >
                  Remove
                </button>
              )}
            </div>

            <div className="form-group">
              <label>Medicine Name:</label>
              <input
                type="text"
                value={medicine.medicineName}
                onChange={(e) => handleMedicineChange(index, 'medicineName', e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Dosage:</label>
              <input
                type="text"
                value={medicine.dosage}
                onChange={(e) => handleMedicineChange(index, 'dosage', e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Quantity:</label>
              <input
                type="number"
                value={medicine.quantity}
                onChange={(e) => handleMedicineChange(index, 'quantity', e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Frequency:</label>
              <input
                type="text"
                value={medicine.frequency}
                onChange={(e) => handleMedicineChange(index, 'frequency', e.target.value)}
                required
              />
            </div>

            <div className="form-group">
              <label>Instructions:</label>
              <textarea
                value={medicine.instructions}
                onChange={(e) => handleMedicineChange(index, 'instructions', e.target.value)}
                required
              />
            </div>
          </div>
        ))}
        <button type="button" onClick={addMedicine} className="add-btn">
          Add Medicine
        </button>
      </div>

      <div className="form-group">
        <label>Patient ID:</label>
        <input
          type="text"
          value={formData.patientId}
          onChange={(e) => setFormData({ ...formData, patientId: e.target.value })}
          required
        />
      </div>

      <div className="form-group">
        <label>Doctor ID:</label>
        <input
          type="text"
          value={formData.doctorId}
          onChange={(e) => setFormData({ ...formData, doctorId: e.target.value })}
          required
        />
      </div>

      <div className="form-group">
        <label>Valid Until:</label>
        <input
          type="date"
          value={formData.validUntil}
          onChange={(e) => setFormData({ ...formData, validUntil: e.target.value })}
          required
        />
      </div>

      <div className="form-group">
        <label>Notes:</label>
        <textarea
          value={formData.notes}
          onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
          required
        />
      </div>

      <button type="submit" className="submit-btn">Submit Prescription</button>
    </form>
  );
};

export default PrescriptionForm;
