import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import PrescriptionForm from "./prescriptionform";
import PrescriptionView from "./viewprescription"; 

function App() {
  const [prescriptionData, setPrescriptionData] = useState(null); // Store prescription data

  // Function to handle prescription form submission
  const handlePrescriptionSubmit = (data) => {
    setPrescriptionData(data); // Set the prescription data after form submission
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Navigate to="/prescription/new" />} />
          <Route path="/prescription/new" element={<PrescriptionForm onSubmit={handlePrescriptionSubmit} />} />
          <Route
            path="/prescription/view"
            element={prescriptionData ? <PrescriptionView prescriptionData={prescriptionData} /> : <Navigate to="/prescription/new" />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
