import React, { useState } from "react";
import "./prescriptionform.css";
import axios from "axios";

const PrescriptionForm = () => {
  const [prescriptionData, setPrescriptionData] = useState({
    doctorName: "",
    specialty: "",
    patientName: "",
    patientAddress: "",
    dateIssued: "",
    insurance: "",
    diagnosis: "",
    notes: "",
    medicines: [{ medicineName: "", dosage: "", frequency: "", quantity: "", instructions: "" }],
  });

  // Handle input changes
  const handleInputChange = (e, field, index) => {
    const { name, value } = e.target;
    if (name === "medicines") {
      const updatedMedicines = [...prescriptionData.medicines];
      updatedMedicines[index][field] = value;
      setPrescriptionData({ ...prescriptionData, medicines: updatedMedicines });
    } else {
      setPrescriptionData({ ...prescriptionData, [name]: value });
    }
  };

  // Add a new medicine field
  const addMedicine = () => {
    setPrescriptionData({
      ...prescriptionData,
      medicines: [
        ...prescriptionData.medicines,
        { medicineName: "", dosage: "", frequency: "", quantity: "", instructions: "" },
      ],
    });
  };

  // Remove a medicine field
  const removeMedicine = (index) => {
    const updatedMedicines = prescriptionData.medicines.filter((_, i) => i !== index);
    setPrescriptionData({ ...prescriptionData, medicines: updatedMedicines });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // Simple validation for required fields
    const { doctorName, patientName, medicines } = prescriptionData;
    if (!doctorName || !patientName || medicines.some((med) => !med.medicineName || !med.dosage || !med.quantity)) {
      alert("Please fill out all required fields.");
      return;
    }
    
    // Submit the form (e.g., via Axios to your backend API)
    axios
      .post("/api/prescriptions", prescriptionData)
      .then((response) => {
        alert("Prescription submitted successfully!");
        // Reset form or handle response
      })
      .catch((error) => {
        console.error("Error submitting prescription:", error);
      });
  };

  return (
    <div className="container">
      <div className="prescription-card">
        <div className="header">
          <h2>Create Prescription</h2>
        </div>
        
        <form onSubmit={handleSubmit}>
          {/* Doctor Info */}
          <div className="form-group">
            <label>Doctor Name:</label>
            <input
              type="text"
              name="doctorName"
              value={prescriptionData.doctorName}
              onChange={(e) => handleInputChange(e, "doctorName")}
              required
              placeholder="Enter doctor's name"
            />
          </div>
          <div className="form-group">
            <label>Specialty:</label>
            <input
              type="text"
              name="specialty"
              value={prescriptionData.specialty}
              onChange={(e) => handleInputChange(e, "specialty")}
              placeholder="Enter doctor's specialty"
            />
          </div>
          
          {/* Patient Info */}
          <div className="form-group">
            <label>Patient Name:</label>
            <input
              type="text"
              name="patientName"
              value={prescriptionData.patientName}
              onChange={(e) => handleInputChange(e, "patientName")}
              required
              placeholder="Enter patient's name"
            />
          </div>
          <div className="form-group">
            <label>Patient Address:</label>
            <input
              type="text"
              name="patientAddress"
              value={prescriptionData.patientAddress}
              onChange={(e) => handleInputChange(e, "patientAddress")}
              placeholder="Enter patient's address"
            />
          </div>
          <div className="form-group">
            <label>Date Issued:</label>
            <input
              type="date"
              name="dateIssued"
              value={prescriptionData.dateIssued}
              onChange={(e) => handleInputChange(e, "dateIssued")}
            />
          </div>
          <div className="form-group">
            <label>Insurance:</label>
            <input
              type="text"
              name="insurance"
              value={prescriptionData.insurance}
              onChange={(e) => handleInputChange(e, "insurance")}
              placeholder="Enter patient's insurance info"
            />
          </div>
          <div className="form-group">
            <label>Diagnosis:</label>
            <input
              type="text"
              name="diagnosis"
              value={prescriptionData.diagnosis}
              onChange={(e) => handleInputChange(e, "diagnosis")}
              placeholder="Enter diagnosis"
            />
          </div>

          {/* Medicines Section */}
          <div className="medicines-section">
            {prescriptionData.medicines.map((med, index) => (
              <div key={index} className="medicine">
                <div className="form-group">
                  <label>Medicine Name:</label>
                  <input
                    type="text"
                    name="medicines"
                    value={med.medicineName}
                    onChange={(e) => handleInputChange(e, "medicineName", index)}
                    required
                    placeholder="Enter medicine name"
                  />
                </div>
                <div className="form-group">
                  <label>Dosage:</label>
                  <input
                    type="text"
                    name="medicines"
                    value={med.dosage}
                    onChange={(e) => handleInputChange(e, "dosage", index)}
                    required
                    placeholder="Enter dosage"
                  />
                </div>
                <div className="form-group">
                  <label>Frequency:</label>
                  <input
                    type="text"
                    name="medicines"
                    value={med.frequency}
                    onChange={(e) => handleInputChange(e, "frequency", index)}
                    placeholder="Enter frequency"
                  />
                </div>
                <div className="form-group">
                  <label>Quantity:</label>
                  <input
                    type="number"
                    name="medicines"
                    value={med.quantity}
                    onChange={(e) => handleInputChange(e, "quantity", index)}
                    required
                    placeholder="Enter quantity"
                  />
                </div>
                <div className="form-group">
                  <label>Instructions:</label>
                  <textarea
                    name="medicines"
                    value={med.instructions}
                    onChange={(e) => handleInputChange(e, "instructions", index)}
                    placeholder="Enter instructions"
                  />
                </div>
                {prescriptionData.medicines.length > 1 && (
                  <button type="button" onClick={() => removeMedicine(index)}>
                    Remove Medicine
                  </button>
                )}
              </div>
            ))}
            <button type="button" onClick={addMedicine} className="add-medicine">
              Add Medicine
            </button>
          </div>

          {/* Notes Section */}
          <div className="form-group">
            <label>Notes:</label>
            <textarea
              name="notes"
              value={prescriptionData.notes}
              onChange={(e) => handleInputChange(e, "notes")}
              placeholder="Enter additional notes"
            />
          </div>

          {/* Submit Button */}
          <button type="submit" className="submit-button">
            Submit Prescription
          </button>
        </form>
      </div>
    </div>
  );
};

export default PrescriptionForm;
