import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FaBars, FaTimes, FaUserCircle, FaMoon, FaSun } from "react-icons/fa";

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);
  const toggleDropdown = () => setIsDropdownOpen(!isDropdownOpen);
  const toggleDarkMode = () => setDarkMode(!darkMode);

  return (
    <nav className={`p-4 shadow-md ${darkMode ? "bg-gray-900 text-white" : "bg-white text-gray-900"}`}>
      <div className="container mx-auto flex justify-between items-center">
        {/* Logo */}
        <div className="text-xl font-bold">
          <Link to="/">Clinical Management</Link>
        </div>

        {/* Menu Items */}
        <div className="hidden md:flex space-x-6 items-center">
          <Link to="/" className="hover:text-blue-500">Home</Link>
          <div className="relative">
            <button onClick={toggleDropdown} className="hover:text-blue-500 flex items-center">
              About <FaUserCircle className="ml-1" />
            </button>
            {isDropdownOpen && (
              <div className="absolute top-10 left-0 w-40 bg-white text-black shadow-md rounded-md p-2">
                <Link to="/about/history" className="block p-2 hover:bg-gray-200">History</Link>
                <Link to="/about/vision" className="block p-2 hover:bg-gray-200">Vision & Mission</Link>
                <Link to="/about/staff" className="block p-2 hover:bg-gray-200">Staff</Link>
              </div>
            )}
          </div>
          <Link to="/prescription" className="hover:text-blue-500">Prescription</Link>
        </div>

        {/* Dark Mode Toggle */}
        <button onClick={toggleDarkMode} className="ml-4">
          {darkMode ? <FaSun className="text-yellow-400" /> : <FaMoon className="text-gray-600" />}
        </button>

        {/* Mobile Menu Button */}
        <button onClick={toggleMenu} className="md:hidden">
          {isMenuOpen ? <FaTimes size={24} /> : <FaBars size={24} />}
        </button>
      </div>

      {/* Mobile Menu */}
      {isMenuOpen && (
        <div className="md:hidden flex flex-col mt-4 space-y-3 bg-white text-gray-900 p-4 shadow-md">
          <Link to="/" className="hover:text-blue-500">Home</Link>
          <button onClick={toggleDropdown} className="flex items-center justify-between hover:text-blue-500">
            About <FaUserCircle className="ml-1" />
          </button>
          {isDropdownOpen && (
            <div className="pl-4">
              <Link to="/about/history" className="block p-2 hover:bg-gray-200">History</Link>
              <Link to="/about/vision" className="block p-2 hover:bg-gray-200">Vision & Mission</Link>
              <Link to="/about/staff" className="block p-2 hover:bg-gray-200">Staff</Link>
            </div>
          )}
          <Link to="/prescription" className="hover:text-blue-500">Prescription</Link>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
