import React, { useState, useEffect } from "react";
import axios from "axios";
import "./prescriptionform.css";

const PrescriptionForm = ({ doctorId, patientId }) => {
  const [prescriptionData, setPrescriptionData] = useState({
    medicines: [{ medicineName: "", dosage: "", frequency: "", quantity: "", instructions: "" }],
    dateIssued: new Date().toISOString().split("T")[0],
    validUntil: "",
    notes: "",
  });
  const [doctorName, setDoctorName] = useState("");
  const [patientName, setPatientName] = useState("");

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const [doctorRes, patientRes] = await Promise.all([
          axios.get(`/api/users/${doctorId}`),
          axios.get(`/api/users/${patientId}`),
        ]);
        setDoctorName(doctorRes.data.name);
        setPatientName(patientRes.data.name);
      } catch (error) {
        console.error("Error fetching user details", error);
      }
    };
    fetchUserDetails();
  }, [doctorId, patientId]);

  const handleInputChange = (e, field, index = null) => {
    const { name, value } = e.target;
    if (index !== null) {
      const updatedMedicines = prescriptionData.medicines.map((med, i) =>
        i === index ? { ...med, [field]: value } : med
      );
      setPrescriptionData({ ...prescriptionData, medicines: updatedMedicines });
    } else {
      setPrescriptionData({ ...prescriptionData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/prescriptions", { ...prescriptionData, doctorId, patientId });
      alert("Prescription submitted successfully!");
    } catch (error) {
      console.error("Error submitting prescription", error);
    }
  };

  return (
    <div className="container">
      <div className="prescription-card">
        <h2>Create Prescription</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Doctor Name:</label>
            <input type="text" value={doctorName} disabled />
          </div>
          <div className="form-group">
            <label>Patient Name:</label>
            <input type="text" value={patientName} disabled />
          </div>
          <div className="form-group">
            <label>Date Issued:</label>
            <input type="date" name="dateIssued" value={prescriptionData.dateIssued} onChange={handleInputChange} />
          </div>
          <div className="form-group">
            <label>Valid Until:</label>
            <input type="date" name="validUntil" value={prescriptionData.validUntil} onChange={handleInputChange} required />
          </div>
          <div className="medicines-section">
            {prescriptionData.medicines.map((med, index) => (
              <div key={index} className="medicine">
                {["medicineName", "dosage", "frequency", "quantity", "instructions"].map((field) => (
                  <input
                    key={field}
                    type={field === "quantity" ? "number" : "text"}
                    name="medicines"
                    value={med[field]}
                    onChange={(e) => handleInputChange(e, field, index)}
                    placeholder={field.charAt(0).toUpperCase() + field.slice(1)}
                    required={field !== "frequency"}
                  />
                ))}
                {prescriptionData.medicines.length > 1 && (
                  <button type="button" onClick={() => removeMedicine(index)}>
                    Remove
                  </button>
                )}
              </div>
            ))}
            <button type="button" onClick={() => addMedicine()} className="add-medicine">
              Add Medicine
            </button>
          </div>
          <div className="form-group">
            <label>Notes:</label>
            <textarea name="notes" value={prescriptionData.notes} onChange={handleInputChange} placeholder="Enter additional notes" required />
          </div>
          <button type="submit" className="submit-button">Submit Prescription</button>
        </form>
      </div>
    </div>
  );

  function addMedicine() {
    setPrescriptionData({
      ...prescriptionData,
      medicines: [...prescriptionData.medicines, { medicineName: "", dosage: "", frequency: "", quantity: "", instructions: "" }],
    });
  }

  function removeMedicine(index) {
    const updatedMedicines = prescriptionData.medicines.filter((_, i) => i !== index);
    setPrescriptionData({ ...prescriptionData, medicines: updatedMedicines });
  }
};

export default PrescriptionForm;
