import React from "react";
import "./prescriptionview.css";

const PrescriptionView = ({ prescriptionData }) => {
  return (
    <div className="prescription-view-container">
      <div className="prescription-header">
        <h2>Prescription</h2>
      </div>
      <div className="prescription-details">
        {/* Doctor's Info */}
        <div className="info-section">
          <h3>Doctor's Information</h3>
          <p><strong>Name:</strong> {prescriptionData.doctorName}</p>
          <p><strong>Specialty:</strong> {prescriptionData.specialty}</p>
        </div>

        {/* Patient's Info */}
        <div className="info-section">
          <h3>Patient's Information</h3>
          <p><strong>Name:</strong> {prescriptionData.patientName}</p>
          <p><strong>Address:</strong> {prescriptionData.patientAddress}</p>
          <p><strong>Date Issued:</strong> {prescriptionData.dateIssued}</p>
          <p><strong>Insurance:</strong> {prescriptionData.insurance}</p>
        </div>

        {/* Diagnosis */}
        <div className="info-section">
          <h3>Diagnosis</h3>
          <p>{prescriptionData.diagnosis}</p>
        </div>

        {/* Medicines */}
        <div className="medicines-section">
          <h3>Medicines</h3>
          {prescriptionData.medicines.map((med, index) => (
            <div key={index} className="medicine-details">
              <p><strong>Medicine Name:</strong> {med.medicineName}</p>
              <p><strong>Dosage:</strong> {med.dosage}</p>
              <p><strong>Frequency:</strong> {med.frequency}</p>
              <p><strong>Quantity:</strong> {med.quantity}</p>
              <p><strong>Instructions:</strong> {med.instructions}</p>
            </div>
          ))}
        </div>

        {/* Additional Notes */}
        {prescriptionData.notes && (
          <div className="notes-section">
            <h3>Notes</h3>
            <p>{prescriptionData.notes}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default PrescriptionView;
