import React, { useState } from 'react';
import './prescriptionF.css';

const PrescriptionForm = () => {
  const [formData, setFormData] = useState({
    medicines: [{
      medicineName: '',
      dosage: '',
      quantity: '',
      frequency: '',
      instructions: ''
    }],
    patientId: '',
    doctorId: '',
    dateIssued: new Date().toISOString().split('T')[0],
    validUntil: '',
    notes: ''
  });

  const handleInputChange = (e, index = null) => {
    const { name, value } = e.target;
    if (index !== null) {
      const updatedMedicines = [...formData.medicines];
      updatedMedicines[index][name] = value;
      setFormData(prevState => ({ ...prevState, medicines: updatedMedicines }));
    } else {
      setFormData(prevState => ({ ...prevState, [name]: value }));
    }
  };

  const addMedicine = () => {
    setFormData(prevState => ({
      ...prevState,
      medicines: [...prevState.medicines, {
        medicineName: '',
        dosage: '',
        quantity: '',
        frequency: '',
        instructions: ''
      }]
    }));
  };

  return (
    <div className="prescription-container">
      <h2>Prescription Form</h2>
      <form>
        {formData.medicines.map((medicine, index) => (
          <div key={index} className="medicine-inputs">
            <input
              type="text"
              name="medicineName"
              placeholder="Medicine Name"
              value={medicine.medicineName}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="text"
              name="dosage"
              placeholder="Dosage"
              value={medicine.dosage}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="number"
              name="quantity"
              placeholder="Quantity"
              value={medicine.quantity}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="text"
              name="frequency"
              placeholder="Frequency"
              value={medicine.frequency}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="text"
              name="instructions"
              placeholder="Instructions"
              value={medicine.instructions}
              onChange={(e) => handleInputChange(e, index)}
            />
          </div>
        ))}
        <button type="button" onClick={addMedicine} className="add-medicine-btn">Add Medicine</button>

        <input
          type="text"
          name="patientId"
          placeholder="Patient ID"
          value={formData.patientId}
          onChange={handleInputChange}
          className="input-field"
        />
        <input
          type="text"
          name="doctorId"
          placeholder="Doctor ID"
          value={formData.doctorId}
          onChange={handleInputChange}
          className="input-field"
        />
        <input
          type="date"
          name="validUntil"
          value={formData.validUntil}
          onChange={handleInputChange}
          className="input-field"
        />
        <textarea
          name="notes"
          placeholder="Additional Notes"
          value={formData.notes}
          onChange={handleInputChange}
          className="input-field textarea-field"
        />
        <button type="submit" className="submit-btn">Submit</button>
      </form>
    </div>
  );
};

export default PrescriptionForm;
